import React from 'react';

const SponsorsSection = () => {
  const sponsorImages = [
    require('../images'),
    require('./path/to/sponsor2.png'),
    // Add more paths as needed
  ];

  const containerStyles = {
    display: 'flex',
    justifyContent: 'space-around', // Adjust as needed
    alignItems: 'center', // Adjust as needed
  };

  const imageStyles = {
    maxWidth: '100%', // Make sure images don't exceed their original size
    height: 'auto', // Maintain aspect ratio
    margin: '10px', // Adjust spacing between images
    border: '1px solid #ddd', // Optional: Add a border around each image
    borderRadius: '8px', // Optional: Add border radius for rounded corners
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Optional: Add a subtle box shadow
  };

  return (
    <div style={containerStyles}>
      {sponsorImages.map((image, index) => (
        <img key={index} src={image.default} alt={`Sponsor ${index + 1}`} style={imageStyles} />
      ))}
    </div>
  );
};

export default SponsorsSection;
