import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Form, Button } from "react-bootstrap";
import Header from "./Header";
import Footer from "./Footer";

export const SignUp = ({ authenticate }) => {
  const navigate = useNavigate();
  const [loginUsername, setLoginUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");

  const [signupUsername, setSignupUsername] = useState("");
  const [signupPassword, setSignupPassword] = useState("");

  const onLoginClick = () => {
    authenticate();
    navigate("/");
  };

  const onSignUpClick = () => {
    navigate("/auth");
  };

  return (
    <>
      <Header />
      <div style={{ width: "300px", margin: "auto", textAlign: "center", marginTop: "200px" }}>
        <h2>User Login</h2>
        <Form>
          <Form.Group controlId="formLoginUsername">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your username"
              value={loginUsername}
              onChange={(e) => setLoginUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formLoginPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter your password"
              value={loginPassword}
              onChange={(e) => setLoginPassword(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" onClick={onLoginClick} style={{ width: "100px" }}>
            Log In
          </Button>
        </Form>

        <div className="p-3"></div>

        <h2>User Signup</h2>
        <Form>
          <Form.Group controlId="formSignupUsername">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              placeholder="Choose a username"
              value={signupUsername}
              onChange={(e) => setSignupUsername(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formSignupPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Choose a password"
              value={signupPassword}
              onChange={(e) => setSignupPassword(e.target.value)}
            />
          </Form.Group>
          <Button variant="primary" onClick={onSignUpClick} style={{ width: "100px" }}>
            Sign Up
          </Button>
        </Form>

        <p>
          Already a user?{" "}
          <span style={{ color: "blue", cursor: "pointer" }} onClick={() => navigate("/login")}>
            Log In
          </span>
        </p>
      </div>
      <Footer />
    </>
  );
};
