import React, { useState } from "react";
import { useHistory } from "react-router-dom";

const RegistrationForm = () => {
  const history = useHistory();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [email, setEmail] = useState("");

  const redirectToLogin = () => {
    // You can perform any additional actions before redirecting, e.g., form validation
    // For now, just redirecting to login
    history.push("/login");
  };

  return (
    <div className="container register-form" style={{ backgroundColor: "white", marginLeft: "28%" }}>
      <div className="form" style={{ marginTop: "300px", marginBottom: "350px" }}>
        <div className="note" style={{ textAlign: "center", paddingTop: "15px", width: "800px" }}>
          <img src="/images/icon.png" alt="Icon" style={{ width: "50px", height: "50px" }} />
          <h1>User Registration</h1>
        </div>
        <div className="form-content" style={{ width: "800px", height: "350px" }}>
          <div className="row">
            <div className="col-md-6">
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Username *"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="password"
                  className="form-control"
                  placeholder="Password *"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  className="form-control"
                  placeholder="Phone Number *"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="email"
                  className="form-control"
                  placeholder="Email Address *"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <button type="button" className="btnSubmit" onClick={redirectToLogin}>
                  Sign Up
                </button>
              </div>
              <div className="form-group">
                <p style={{ color: "black" }}>
                  Already have an account? <a href="/login" style={{ color: "blue" }}>
                    Login
                  </a>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegistrationForm;
